CREATE PROCEDURE OMZETGEGEVENS_UPDATE (
 WAG_ID integer,
 DATUM date,
 WAARDE decimal(10,4) )
RETURNS (
 OWAG_ID integer,
 ODATUM date,
 OWAARDE decimal(10,4) )
AS
BEGIN
 update or insert into omzetgegevens values(:wag_id,:datum,:waarde);
 owag_id = :wag_id;
 odatum = :datum;
 owaarde = :waarde;
 suspend;
END^
SET TERM ; ^
GRANT EXECUTE
 ON PROCEDURE OMZETGEGEVENS_UPDATE TO SYSDBA;

GRANT EXECUTE
 ON PROCEDURE OMZETGEGEVENS_UPDATE TO SYSDBA;

