CREATE PROCEDURE WEEKINFO (
 JAARWEEK integer )
RETURNS (
 AFDELING varchar(45),
 OMZETGROEP varchar(45),
 NORMOMZET decimal(14,0),
 WEEKOMZET decimal(14,0),
 PERIODEOMZET decimal(14,0),
 JAAROMZET decimal(14,0),
 VORIGJAAROMZET decimal(14,0),
 NORMDERVING decimal(14,0),
 WEEKDERVING decimal(14,0),
 PERIODEDERVING decimal(14,0),
 JAARDERVING decimal(14,0),
 VORIGJAARDERVING decimal(14,0) )
AS
DECLARE VARIABLE omzetgroep_id integer;
DECLARE VARIABLE afdeling_id integer;
declare variable vorigjaarstartweek integer;
declare variable vorigjaareindweek integer;
BEGIN
 select first 1 f_datetoyearweek(o.datum) from omzetgegevens o where f_datetoyearweek(o.datum)/100 = :jaarweek/100-1 group by f_datetoyearweek(o.datum) order by f_datetoyearweek(o.datum) asc into :vorigjaarstartweek;
 select first 1 f_datetoyearweek(o.datum) from omzetgegevens o where f_datetoyearweek(o.datum)/100 = :jaarweek/100-1 group by f_datetoyearweek(o.datum) order by f_datetoyearweek(o.datum) desc into :vorigjaareindweek;
 for select omschrijving,g.OMZETGROEP_ID, afdeling_id from omzetgroep g order by afdeling_id into :omzetgroep,:omzetgroep_id,afdeling_id do
 begin
 select omschrijving from afdeling where afdeling_id = :afdeling_id into :afdeling;
 select sum(waarde) from omzetgegevens o join wag w on o.WAG_ID = w.WAG_ID where f_datetoyearweek(o.DATUM)= :jaarweek and w.OMZETGROEP_ID = :omzetgroep_id group by w.omzetgroep_id into weekomzet;
 select sum(waarde)/(:jaarweek-(:jaarweek-mod(:jaarweek-1,4))+1) from omzetgegevens o join wag w on o.WAG_ID = w.WAG_ID where (f_datetoyearweek(o.DATUM)\g= :jaarweek-mod(:jaarweek-1,4) and f_datetoyearweek(o.DATUM)\k= :jaarweek) and w.OMZETGROEP_ID = :omzetgroep_id group by w.omzetgroep_id into periodeomzet;
 select sum(waarde)/(:jaarweek-100-(:jaarweek-100-mod(:jaarweek-100-1,4))+1) from omzetgegevens o join wag w on o.WAG_ID = w.WAG_ID where (f_datetoyearweek(o.DATUM)\g= :jaarweek-100-mod(:jaarweek-100-1,4) and f_datetoyearweek(o.DATUM)\k= :jaarweek-100) and w.OMZETGROEP_ID = :omzetgroep_id group by w.omzetgroep_id into :jaaromzet;
 /*select sum(waarde)/(:jaarweek-(:jaarweek/100*100+1)+1) from omzetgegevens o join wag w on o.WAG_ID = w.WAG_ID where (f_datetoyearweek(o.DATUM)\g= (:jaarweek/100*100+1) and f_datetoyearweek(o.DATUM)\k= :jaarweek) and w.OMZETGROEP_ID = :omzetgroep_id group by w.omzetgroep_id into jaaromzet;*/
 select sum(waarde)/(:vorigjaareindweek-:vorigjaarstartweek+1) from omzetgegevens o join wag w on o.WAG_ID = w.WAG_ID where (f_datetoyearweek(o.DATUM)\g= :vorigjaarstartweek and f_datetoyearweek(o.DATUM)\k= :vorigjaareindweek) and w.OMZETGROEP_ID = :omzetgroep_id group by w.omzetgroep_id into vorigjaaromzet;
 suspend;
 end
END^
SET TERM ; ^
GRANT EXECUTE
 ON PROCEDURE WEEKINFO TO SYSDBA;

commit;

