CREATE PROCEDURE VOORRAADCORRECTIES_UPDATE (
 OMZETGROEP varchar(45),
 SOORT varchar(45),
 ARTIKELNUMMER integer,
 OMSCHRIJVING varchar(45),
 AANTAL decimal(10,4),
 WAARDE decimal(10,4) )
RETURNS (
 OOMZETGROEP varchar(45),
 OSOORT varchar(45),
 OARTIKELNUMMER integer,
 OOMSCHRIJVING varchar(45),
 OAANTAL decimal(10,4),
 OWAARDE decimal(10,4),
 OMZETGROEP_ID integer,
 SOORT_ID integer )
AS
declare variable l_omzetgroep varchar(45);
BEGIN
 l_omzetgroep = :omzetgroep;
 if (l_omzetgroep = \aVerkoop Emballage\a) then
 begin
 l_omzetgroep = \aDKW\a;
 end
 select o.omzetgroep_id from omzetgroep o where :l_omzetgroep = o.OMSCHRIJVING into :omzetgroep_id;
 if (:omzetgroep_id is null) then
 begin
 oomzetgroep = :l_omzetgroep || \a \a || \aFOUT\a;
 suspend;
 exit;
 end
 select s.SOORT_ID from soort s where :soort = s.OMSCHRIJVING into :soort_id;
 if (:soort_id is null) then
 begin
 insert into soort values(null,:soort);
 select s.SOORT_ID from soort s where :soort = s.OMSCHRIJVING into :soort_id;
 end
 if (omzetgroep_id is not null) then
 begin
 insert into voorraadcorrecties values(null,:omzetgroep_id,:soort_id,\a13.10.2012\a,:artikelnummer,:omschrijving,:aantal,:waarde);
 end
 oomzetgroep = l_omzetgroep;
 osoort = soort;
 oartikelnummer = artikelnummer;
 oomschrijving = omschrijving;
 oaantal = aantal;
 owaarde = waarde;
 suspend;
END^
SET TERM ; ^
GRANT EXECUTE
 ON PROCEDURE VOORRAADCORRECTIES_UPDATE TO SYSDBA;

commit;
